import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { Edit3, Eye } from "lucide-react";
import EditIdModal from "@/components/modals/edit-id-modal";
import type { Patient, InsertPatient } from "@shared/schema";

export default function Patients() {
  const [formData, setFormData] = useState({
    patientId: "",
    name: "",
    age: "",
    gender: "",
    phone: "",
    address: "",
  });
  const [editingPatientId, setEditingPatientId] = useState<number | null>(null);

  const { toast } = useToast();

  // Get next patient ID
  const { data: nextIdData } = useQuery<{ nextId: string }>({
    queryKey: ["/api/patients/next-id"],
  });

  // Get all patients
  const { data: patients = [], isLoading } = useQuery<Patient[]>({
    queryKey: ["/api/patients"],
  });

  // Create patient mutation
  const createPatientMutation = useMutation({
    mutationFn: async (data: InsertPatient) => {
      const response = await apiRequest("POST", "/api/patients", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/patients"] });
      queryClient.invalidateQueries({ queryKey: ["/api/patients/next-id"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      setFormData({
        patientId: "",
        name: "",
        age: "",
        gender: "",
        phone: "",
        address: "",
      });
      toast({
        title: "Patient registered successfully",
        description: "New patient has been added to the system",
      });
    },
  });

  // Update patient ID mutation
  const updatePatientIdMutation = useMutation({
    mutationFn: async ({ id, newPatientId }: { id: number; newPatientId: string }) => {
      const response = await apiRequest("PUT", `/api/patients/${id}/patient-id`, { newPatientId });
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/patients"] });
      toast({
        title: "Patient ID updated successfully",
        description: "Patient ID has been changed",
      });
    },
  });

  // Set initial patient ID when component loads
  React.useEffect(() => {
    if (nextIdData?.nextId && !formData.patientId) {
      setFormData(prev => ({ ...prev, patientId: nextIdData.nextId }));
    }
  }, [nextIdData, formData.patientId]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!formData.patientId || !formData.name) {
      toast({
        title: "Validation Error",
        description: "Patient ID and Name are required",
        variant: "destructive",
      });
      return;
    }

    try {
      await createPatientMutation.mutateAsync({
        patientId: formData.patientId,
        name: formData.name,
        age: formData.age ? parseInt(formData.age) : null,
        gender: formData.gender || null,
        phone: formData.phone || null,
        address: formData.address || null,
        createdBy: undefined,
        modifiedBy: undefined,
      });
    } catch (error: any) {
      toast({
        title: "Registration Failed",
        description: error.message || "Failed to register patient",
        variant: "destructive",
      });
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleGenderChange = (value: string) => {
    setFormData(prev => ({ ...prev, gender: value }));
  };

  const handleEditId = (patientId: number) => {
    setEditingPatientId(patientId);
  };

  const handleIdUpdate = async (newId: string) => {
    if (editingPatientId) {
      try {
        await updatePatientIdMutation.mutateAsync({
          id: editingPatientId,
          newPatientId: newId,
        });
        setEditingPatientId(null);
      } catch (error: any) {
        toast({
          title: "Update Failed",
          description: error.message || "Failed to update patient ID",
          variant: "destructive",
        });
      }
    }
  };

  return (
    <div className="p-8">
      <div className="mb-8">
        <h1 className="text-2xl font-bold text-slate-800">Patient Management</h1>
        <p className="text-slate-600">Register and manage patient information</p>
      </div>

      {/* Patient Registration Form */}
      <Card className="mb-8">
        <CardHeader>
          <CardTitle>Patient Registration</CardTitle>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {/* Patient ID with Edit Button */}
            <div className="col-span-1">
              <Label className="block text-sm font-medium text-slate-700 mb-2">Patient ID</Label>
              <div className="flex">
                <Input
                  type="text"
                  name="patientId"
                  value={formData.patientId}
                  onChange={handleInputChange}
                  readOnly
                  className="flex-1 rounded-r-none bg-slate-50 text-slate-600"
                />
                <Button
                  type="button"
                  className="rounded-l-none bg-[var(--medical-primary)] hover:bg-[var(--medical-primary-dark)]"
                  onClick={() => handleEditId(0)} // Use 0 for new patient ID editing
                >
                  <Edit3 className="h-4 w-4" />
                </Button>
              </div>
            </div>

            <div>
              <Label className="block text-sm font-medium text-slate-700 mb-2">Full Name</Label>
              <Input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                required
                placeholder="Enter patient name"
              />
            </div>

            <div>
              <Label className="block text-sm font-medium text-slate-700 mb-2">Age</Label>
              <Input
                type="number"
                name="age"
                value={formData.age}
                onChange={handleInputChange}
                placeholder="Enter age"
              />
            </div>

            <div>
              <Label className="block text-sm font-medium text-slate-700 mb-2">Gender</Label>
              <Select value={formData.gender} onValueChange={handleGenderChange}>
                <SelectTrigger>
                  <SelectValue placeholder="Select gender" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="male">Male</SelectItem>
                  <SelectItem value="female">Female</SelectItem>
                  <SelectItem value="other">Other</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label className="block text-sm font-medium text-slate-700 mb-2">Phone Number</Label>
              <Input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                placeholder="Enter phone number"
              />
            </div>

            <div className="col-span-1 md:col-span-2 lg:col-span-3">
              <Label className="block text-sm font-medium text-slate-700 mb-2">Address</Label>
              <Textarea
                name="address"
                value={formData.address}
                onChange={handleInputChange}
                rows={3}
                placeholder="Enter complete address"
              />
            </div>

            <div className="col-span-1 md:col-span-2 lg:col-span-3">
              <Button
                type="submit"
                disabled={createPatientMutation.isPending}
                className="bg-[var(--medical-primary)] hover:bg-[var(--medical-primary-dark)] text-white"
              >
                {createPatientMutation.isPending ? "Registering..." : "Register Patient"}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>

      {/* Patient List */}
      <Card>
        <CardHeader>
          <CardTitle>Registered Patients</CardTitle>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex justify-center py-8">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Patient ID</TableHead>
                    <TableHead>Name</TableHead>
                    <TableHead>Age</TableHead>
                    <TableHead>Gender</TableHead>
                    <TableHead>Phone</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {patients.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={6} className="text-center py-8 text-slate-500">
                        No patients registered yet
                      </TableCell>
                    </TableRow>
                  ) : (
                    patients.map((patient) => (
                      <TableRow key={patient.id}>
                        <TableCell className="font-medium">{patient.patientId}</TableCell>
                        <TableCell>{patient.name}</TableCell>
                        <TableCell>{patient.age || "-"}</TableCell>
                        <TableCell className="capitalize">{patient.gender || "-"}</TableCell>
                        <TableCell>{patient.phone || "-"}</TableCell>
                        <TableCell>
                          <div className="flex space-x-2">
                            <Button variant="ghost" size="sm">
                              <Eye className="h-4 w-4" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleEditId(patient.id)}
                            >
                              <Edit3 className="h-4 w-4" />
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </div>
          )}
        </CardContent>
      </Card>

      <EditIdModal
        isOpen={editingPatientId !== null}
        onClose={() => setEditingPatientId(null)}
        currentId={editingPatientId ? patients.find(p => p.id === editingPatientId)?.patientId || "" : formData.patientId}
        idType="Patient"
        onUpdate={handleIdUpdate}
      />
    </div>
  );
}

client/src/components/auth/change-passwo.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { KeyRound, AlertCircle } from "lucide-react";

interface ChangePasswordModalProps {
  isOpen: boolean;
  onClose: () => void;
}

export default function ChangePasswordModal({ isOpen, onClose }: ChangePasswordModalProps) {
  const [formData, setFormData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [error, setError] = useState("");

  const { changePassword, isChangePasswordPending } = useAuth();
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");

    if (formData.newPassword !== formData.confirmPassword) {
      setError("Passwords don't match");
      return;
    }

    if (formData.newPassword.length < 8) {
      setError("Password must be at least 8 characters");
      return;
    }

    try {
      await changePassword({
        currentPassword: formData.currentPassword,
        newPassword: formData.newPassword,
        confirmPassword: formData.confirmPassword,
      });

      toast({
        title: "Password changed successfully",
        description: "Your password has been updated",
      });

      setFormData({
        currentPassword: "",
        newPassword: "",
        confirmPassword: "",
      });
      onClose();
    } catch (error: any) {
      setError(error.message || "Failed to change password");
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleClose = () => {
    setFormData({
      currentPassword: "",
      newPassword: "",
      confirmPassword: "",
    });
    setError("");
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <div className="flex items-center space-x-2">
            <div className="h-12 w-12 bg-[var(--medical-primary)]/10 rounded-full flex items-center justify-center">
              <KeyRound className="h-6 w-6 text-[var(--medical-primary)]" />
            </div>
            <div>
              <DialogTitle className="text-lg font-semibold text-slate-800">
                Change Password
              </DialogTitle>
              <p className="text-sm text-slate-600">Update your admin password</p>
            </div>
          </div>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <Label htmlFor="currentPassword" className="block text-sm font-medium text-slate-700 mb-2">
              Current Password
            </Label>
            <Input
              type="password"
              id="currentPassword"
              name="currentPassword"
              required
              value={formData.currentPassword}
              onChange={handleInputChange}
              placeholder="Enter current password"
            />
          </div>

          <div>
            <Label htmlFor="newPassword" className="block text-sm font-medium text-slate-700 mb-2">
              New Password
            </Label>
            <Input
              type="password"
              id="newPassword"
              name="newPassword"
              required
              value={formData.newPassword}
              onChange={handleInputChange}
              placeholder="Enter new password"
            />
          </div>

          <div>
            <Label htmlFor="confirmPassword" className="block text-sm font-medium text-slate-700 mb-2">
              Confirm New Password
            </Label>
            <Input
              type="password"
              id="confirmPassword"
              name="confirmPassword"
              required
              value={formData.confirmPassword}
              onChange={handleInputChange}
              placeholder="Confirm new password"
            />
          </div>

          {error && (
            <Alert className="border-red-200 bg-red-50">
              <AlertCircle className="h-4 w-4 text-red-500" />
              <AlertDescription className="text-red-600">
                {error}
              </AlertDescription>
            </Alert>
          )}

          <div className="flex space-x-3 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              className="flex-1"
            >
              Cancel
            </Button>
            <Button
              type="submit"
              disabled={isChangePasswordPending}
              className="flex-1 medical-gradient text-white"
            >
              {isChangePasswordPending ? "Updating..." : "Update Password"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/auth/login-form.ts.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useAuth } from "@/hooks/use-auth";
import { useToast } from "@/hooks/use-toast";
import { AlertCircle, Eye, EyeOff, ShieldCheck } from "lucide-react";

interface LoginFormProps {
  onChangePassword: () => void;
}

export default function LoginForm({ onChangePassword }: LoginFormProps) {
  const [formData, setFormData] = useState({
    username: "",
    password: "",
    rememberMe: false,
  });
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");

  const { login, isLoginPending } = useAuth();
  const { toast } = useToast();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");

    try {
      await login(formData);
      toast({
        title: "Login successful",
        description: "Welcome to Laboratory Management System",
      });
    } catch (error: any) {
      setError(error.message || "Login failed");
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <Label htmlFor="username" className="block text-sm font-medium text-slate-700 mb-2">
          Username
        </Label>
        <Input
          type="text"
          id="username"
          name="username"
          required
          value={formData.username}
          onChange={handleInputChange}
          className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-[var(--medical-primary)] focus:border-[var(--medical-primary)] transition-colors"
          placeholder="Enter your username"
        />
      </div>

      <div>
        <Label htmlFor="password" className="block text-sm font-medium text-slate-700 mb-2">
          Password
        </Label>
        <div className="relative">
          <Input
            type={showPassword ? "text" : "password"}
            id="password"
            name="password"
            required
            value={formData.password}
            onChange={handleInputChange}
            className="w-full px-4 py-3 border border-slate-300 rounded-lg focus:ring-2 focus:ring-[var(--medical-primary)] focus:border-[var(--medical-primary)] transition-colors pr-12"
            placeholder="Enter your password"
          />
          <Button
            type="button"
            variant="ghost"
            size="sm"
            className="absolute inset-y-0 right-0 pr-3 flex items-center"
            onClick={() => setShowPassword(!showPassword)}
          >
            {showPassword ? (
              <EyeOff className="h-5 w-5 text-slate-400 hover:text-slate-600" />
            ) : (
              <Eye className="h-5 w-5 text-slate-400 hover:text-slate-600" />
            )}
          </Button>
        </div>
      </div>

      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Checkbox
            id="rememberMe"
            name="rememberMe"
            checked={formData.rememberMe}
            onCheckedChange={(checked) => 
              setFormData(prev => ({ ...prev, rememberMe: !!checked }))
            }
          />
          <Label htmlFor="rememberMe" className="text-sm text-slate-600">
            Remember me
          </Label>
        </div>
        <Button
          type="button"
          variant="link"
          className="text-sm text-[var(--medical-primary)] hover:text-[var(--medical-primary-dark)] p-0"
          onClick={onChangePassword}
        >
          Change Password
        </Button>
      </div>

      <Button
        type="submit"
        disabled={isLoginPending}
        className="w-full medical-gradient text-white py-3 px-4 rounded-lg font-medium hover:opacity-90 transition-all duration-200 transform hover:scale-[1.02] focus:ring-4 focus:ring-[var(--medical-primary)]/25"
      >
        {isLoginPending ? (
          <div className="flex items-center justify-center">
            <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
            Signing In...
          </div>
        ) : (
          <div className="flex items-center justify-center">
            <ShieldCheck className="h-5 w-5 mr-2" />
            Sign In
          </div>
        )}
      </Button>

      {error && (
        <Alert className="border-red-200 bg-red-50">
          <AlertCircle className="h-4 w-4 text-red-500" />
          <AlertDescription className="text-red-600">
            {error}
          </AlertDescription>
        </Alert>
      )}
    </form>
  );
}

