        description: "Test results have been recorded",
      });
    },
  });

  // Set initial test ID when component loads
  React.useEffect(() => {
    if (nextIdData?.nextId && !formData.testId) {
      setFormData(prev => ({ ...prev, testId: nextIdData.nextId }));
    }
  }, [nextIdData, formData.testId]);

